package codigoCaracteres;

public class ConversionEntreCharacter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       char letra1='A',letra2;
       letra2=Character.toLowerCase(letra1);
       
       
       System.err.println(letra2);
       char letra3='b',letra4;
       letra4=Character.toUpperCase(letra3);
       System.out.println(letra4);
       
    		   
	}

}
